import React from 'react'
import { useParams, Navigate } from 'react-router-dom'
import { getHeroById } from '../../selectors/getHeroById';

export const HeroScreen = () => {

    const { heroeId } = useParams(); // Hook de Router que regresa un arrelo con los parametros del path

    const hero = getHeroById(heroeId);

    const {
        id,
        superhero,
        publisher,
        alter_ego,
        first_appearance,
        characters
    } = hero;

    if( !hero ) { return <Navigate to='/' /> } // Navigate es un componente ya creado por React Router 

    const imagePath = `/assets/${hero.id}.jpg`;

    return (
        <div className='row mt-5'>
            <div className='col-4'>
                <img src={ imagePath } alt={ hero.superhero } className='image-thumbnail'/>
            </div>
            <div className='col-8'>

            </div>
        </div>
    )
}
