import React from 'react'
import { useParams, Navigate } from 'react-router-dom'
import { getHeroById } from '../../selectors/getHeroById';

export const HeroScreen = () => {

    const { heroeId } = useParams(); // Hook de Router que regresa un arrelo con los parametros del path

    const hero = getHeroById(heroeId);

    const handleReturn = () => {
        if(publisher === 'DC Comics'){ <Navigate to='dc' /> }else{ <Navigate to='marvel' /> } // Navigate es un componente ya creado por React Router 
    }

    const {
        id,
        superhero,
        publisher,
        alter_ego,
        first_appearance,
        characters
    } = hero;

    if( !hero ) { return <Navigate to='/' /> } // Navigate es un componente ya creado por React Router 

    const imagePath = `/assets/${hero.id}.jpg`;

    return (
        <div className='row mt-5'>
            <div className='col-4'>
                <img src={ imagePath } alt={ hero.superhero } className='image-thumbnail'/>
            </div>
            <div className='col-8'>
                <h3>{ superhero }</h3>
                <ul className='list-group'>
                    <li className='list-group-item'> <b>Alter ego:</b> { alter_ego } </li>
                    <li className='list-group-item'> <b>Publisher:</b> { publisher } </li>
                    <li className='list-group-item'> <b>First Appearance:</b> { first_appearance } </li>
                </ul>

                <h5 className='mt-3'>Characters</h5>
                <p> { characters } </p>

                <button className='btn btn-outline-info' onClick={ handleReturn }> Regresar </button>

            </div>
        </div>
    )
}
